m长度的都是n的数组



var a = initArray(10, 1);
console.log(a);
function initArray(m, n) {
  var aa = [];
  (function loop(m, n) {
    if (m > 0) {
      aa[m - 1] = loop(m - 1, n);
    }
    return n;
  })(m, n);
  return aa;
}
