scripto/简单的模板引擎.html.body:




简单的模板引擎




// 之间可以放置任意的 JavaScript 代码
// 之间执行任意的 JavaScript 表达式并且输出在模版上
//传入的 data 可以作为模版引擎执行的上下文进行数据的引用
const templateStr = `

   { %>






`;
const data = {
  users: [
    { name: "Jerry", age: 12 },
    { name: "Lucy", age: 13 },
    { name: "Tomy", age: 14 }
  ]
};
let hm = render(templateStr, data);
document.getElementsByTagName("body")[0].innerHTML = hm;
function render(template, data) {
  const reg_1 = //g; //匹配JavaScript 代码
  const reg_2 = //g; //匹配JavaScript 表达式
  let code_str = [];
  let match;
  let cursor = 0;
  code_str.push("const list = [];\r\n");
  // code_str.push("const users = arguments[0];\r\n");
  // code_str.push("console.log(arguments);\r\n");
  while ((match = reg_1.exec(template))) {
    code_str.push(
      `list.push(\`${template.slice(cursor, match.index)}\`);\r\n`
    );
    code_str.push(`${match[1]}\r\n`);
    cursor = match.index + match[0].length; //起始点 = 找到的字符开始下标 + 字符的长度
  }
  code_str.push(`list.push(\`${template.slice(cursor)}\`);\r\n`);
  code_str = code_str.join("").replace(reg_2, ($1, match) => {
    return "${" + match + "}";
  });
  code_str += "return list.join('')";
  return new Function(`with(this){${code_str}}`).call(data);
}

scripto/中间件模式.html.body:




中间件模式



//http://scriptoj.mangojuice.top/problems/52
const app = {
  middlewares: [],
  callback(ctx) {
    console.log(ctx);
  },
  //中间件是一个函数，可以被传入一个 ctx 和 next
  //调用 next 的时候会执行下一个中间件,如果不调用 next 会阻止接下来所有的中间件的执行，也不会执行 app.callback
  use(fn) {
    this.middlewares.push(fn);
  },
  go(ctx) {
    let index = 0;
    const next = () => {
      index++;
    };
    this.middlewares.forEach((md, current) => {
      if (index === current) md(ctx, next);
    });
    index === this.middlewares.length && this.callback(ctx);
  }
};
app.use((ctx, next) => {
  ctx.name = "lucy";
  next();
});
app.use((ctx, next) => {
  ctx.age = 12;
  next();
});
app.use((ctx, next) => {
  console.log(`${ctx.name} is ${ctx.age} year old`);
  next();
});
app.go({});
app.use((ctx, next) => {
  ctx.version = "10.0.0";
  next();
});
app.go({});

